import models from "../models"

export default {
  register: async(req, res) =>{
    try{
      let valid_observacion = await models.Observaciones.find({nombre: req.body.nombre});
      if(valid_observacion.length > 0){
        res.status(200).json({
          code: 403,
          message: 'La observacion ya existe ya existe'
        });
        return;
      }
      const observaciones = await models.Observaciones.create(req.body)
      res.status(200).json(observaciones)
    }catch(err){
      res.status(500).send({message: 'ocurrio un problema'})
      console.log(err)
    }
  },
  update: async(req, res) =>{
    try{
      console.log(req.body)
      await models.Observaciones.findByIdAndUpdate({_id:req.body._id},req.body)

      res.status(200).json({
        message:'observacion modificada',
      })
    }catch(err){
      res.status(500).send({message: 'ocurrio un problema'})
      console.log(err)
    }
  },
  remove: async(req, res) =>{
    try{
      await models.Observaciones.findByIdAndDelete({_id: req.params.id})
      res.status(200).json({message: 'observacion eliminada correctamente' })
    }catch(err){
      console.log(err)
      res.status(500).send({message: 'ocurrio un problema'})
    }
  },
  list: async(req, res) =>{
    try{
      var search = req.query.search
      let observaciones = await models.Observaciones.find({
        $or:[
          {'nombre': new RegExp(search, 'i')},
        ]
      }).sort({'createdAt':-1})

      res.status(200).json(
        observaciones
      )
    }catch(err){
      res.status(500).send({message: 'ocurrio un problema'})
      console.log(err)
    }
  },
}

