import jwt from 'jsonwebtoken'
import models from '../models'

export default {
  encode: async(lecturista, ruta) =>{
      const token = jwt.sign({lecturista: lecturista, ruta: ruta},'aguacom-',{expiresIn: '1d'});
      return token;
  },
  decode: async(token) => {
      try {
          const {lecturista, ruta} = await jwt.verify(token,'aguacom-');
          const user = models.RutCap.findOne({lecturista: lecturista, ruta: ruta});
          if(user){
              return user; 
          }
          return false;
      } catch (error) {
          console.log(error);
          return false;
      }
  }
}

