import models from "../models"

export default {
  register: async(req, res) =>{
    try{
      let valid_rutcap = await models.RutCap.find({lecturista: req.body.lecturista, ruta: req.body.ruta});
      if(valid_rutcap.length > 0){
        res.status(200).json({
          code: 403,
          message: 'La ruta y lectursta ya existe ya existe'
        });
        return;
      }
      const rutCap = await models.RutCap.create(req.body)
      res.status(200).json(rutCap)
    }catch(err){
      res.status(500).send({message: 'ocurrio un problema'})
      console.log(err)
    }
  },
  update: async(req, res) =>{
    try{
      await models.RutCap.findByIdAndUpdate({_id:req.body._id},req.body)

      res.status(200).json({
        message:'rutcap modificados',
      })
    }catch(err){
      res.status(500).send({message: 'ocurrio un problema'})
      console.log(err)
    }
  },
  remove: async(req, res) =>{
    try{
      await models.RutCap.findByIdAndDelete({_id: req.params.id})
      res.status(200).json({message: 'rutcap eliminada correctamente' })
    }catch(err){
      console.log(err)
      res.status(500).send({message: 'ocurrio un problema'})
    }
  },
  list: async(req, res) =>{
    try{
      var search = req.query.search
      let rutCap = await models.RutCap.find({
        $or:[
          {'ruta': new RegExp(search, 'i')},
        ]
      }).sort({'createdAt':-1})

      res.status(200).json(rutCap)
    }catch(err){
      res.status(500).send({message: 'ocurrio un problema'})
      console.log(err)
    }
  },
}

