sourcesContent:
import models from "../models"

export default {
  register: async(req, res) =>{
    try{

      let valid_lectura = await models.Lecturas.findOne({medidor: req.body.medidor});

      if(valid_lectura ){
        let data = req.body;
        if(req.files && req.files.nom_foto.path){
          var img_path = req.files.nom_foto.path;
          var name = img_path.split('\\');
          var portada_name = name[0];

          data.nom_foto = portada_name;
        }

        await models.Lecturas.findByIdAndUpdate({_id:valid_lectura._id},data)

        res.status(200).json({
          code: 403,
          message: 'La lectura ya existe ya existe, lectura modificada'
        });
        return;
      }

      let data = req.body;
      if(req.files){
        var img_path = req.files.nom_foto.path;
        var name = img_path.split('\\');
        var portada_name = name[0];

        data.nom_foto = portada_name;
      }
      let lectura = await models.Lecturas.create(data)
      res.status(200).json(lectura)
    }catch(err){
      res.status(500).send({message: 'ocurrio un problema', error: err})
      console.log(err)
    }
  },
  update: async(req, res) =>{
    try{
      let data = req.body;
      if(req.files && req.files.nom_foto.path){
        var img_path = req.files.nom_foto.path;
        var name = img_path.split('\\');
        var portada_name = name[0];

        data.nom_foto = portada_name;
      }

      await models.Lecturas.findByIdAndUpdate({_id:data._id},data)

      res.status(200).json({
        message:'lectura modificada',
      })
    }catch(err){
      res.status(500).send({message: 'ocurrio un problema'})
      console.log(err)
    }
  },

  list: async(req, res) =>{
    try{

      if(req.body.page){
        var no_ruta = req.body.no_ruta;
        var page = req.body.page;
        var limit = req.body.limit
        var skip = (page - 1) * limit;

        if(req.body.status){
          let lecturas = await models.Lecturas.find({no_ruta:no_ruta, status: req.body.status}).skip(skip).limit(limit).sort({'createdAt':-1})
          let total = await models.Lecturas.find({no_ruta:no_ruta, status: req.body.status})

          res.status(200).json({
            lecturas: lecturas,
            total:total.length
          })
          return
        }

        let lecturas = await models.Lecturas.find({no_ruta:no_ruta}).skip(skip).limit(limit).sort({'createdAt':-1})
        let total = await models.Lecturas.find({no_ruta:no_ruta})

        res.status(200).json({
          lecturas: lecturas,
          total:total.length
        })
        return
      }

      if(req.body.status){
        let lecturas = await models.Lecturas.find({no_ruta:req.body.no_ruta, status: req.body.status}).sort({'createdAt':-1})
        res.status(200).json(lecturas)
        return
      }

      let lecturas = await models.Lecturas.find({no_ruta:req.body.no_ruta}).sort({'createdAt':-1})
      res.status(200).json(lecturas)

    }catch(err){
      console.log(err)
      res.status(500).send({message: 'ocurrio un problema'})
    }
  },
  remove: async(req, res) =>{
    try{
      await models.Lecturas.findByIdAndDelete({_id: req.params.id})
      res.status(200).json({message: 'lectura eliminada correctamente' })
    }catch(err){
      console.log(err)
      res.status(500).send({message: 'ocurrio un problema'})
    }
  },
}

