import models from "../models"
import token from '../services/token'

export default {
  connectionToken: async(req, res) =>{
    try{
      let [rutCap] = await models.RutCap.find({lecturista: req.body.lecturista, ruta:req.body.ruta})
      let tokenT = await token.encode(rutCap.lecturista, rutCap.ruta)

      res.status(200).json({
        token: tokenT
      })
    }catch(err){
      res.status(500).send({message: 'ocurrio un problema'})
      console.log(err)
    }
  },
}

