import mongoose,{Schema} from "mongoose";

const LecturasSchema = new Schema({
    no_ruta:{type:Number,required:true},
    contrato:{type:Number,required:true},
    num_sec:{type:Number,required:false},
    nom_colonia:{type:String,maxlength : 250,required:false},
    nom_calle:{type:String,maxlength : 250,required:false},
    nesol:{type:String,maxlength : 250,required:false},
    nisol:{type:String,maxlength : 250,required:false},
    lectura:{type:Number,required:false},
    medidor:{type:String,maxlength : 250,required:true},
    observa:{type:Schema.ObjectId,ref:'observaciones',required:false},
    nom_foto:{type:String,maxlength : 250,required:false},
    comentarios:{type:String,maxlength : 250,required:false},
    fecha:{type:String,maxlength : 250,required:false},
    hora:{type:String,maxlength : 250,required:false},
    status:{type:Number,default:1}, // 0-sin revisar 1- revisado
    idlect:{type:Number, required: true}
},{
    timestamps: true
});

const Lecturas = mongoose.model("Lecturas",LecturasSchema);
export default Lecturas;

