import models from "../models"

export default {
  register: async(req, res) =>{
    try{

      let valid_categorie = await models.Categories.findOne({name: req.body.name});

      if(valid_categorie ){
        res.status(400).send({message: 'Ya existe una categoria con ese nombre'})
        return
      }

      let data = req.body;
      if(req.files){
        var img_path = req.files.cover.path;
        var name = img_path.split('\\');
        var portada_name = name[0];

        data.cover= portada_name;
      }
      let categorie = await models.Categories.create(data)
      res.status(200).json(categorie)
    }catch(err){
      res.status(500).send({message: 'ocurrio un problema', error: err})
      console.log(err)
    }
  },
  update: async(req, res) =>{
    try{
      let data = req.body;
      if(req.files && req.files.cover.path){
        var img_path = req.files.cover.path;
        var name = img_path.split('\\');
        var portada_name = name[0];

        data.cover = portada_name;
      }

      await models.Categories.findByIdAndUpdate({_id:data._id},data)

      res.status(200).json({
        message:'categoria modificada',
      })
    }catch(err){
      res.status(500).send({message: 'ocurrio un problema'})
      console.log(err)
    }
  },

  list: async(req, res) =>{
    try{
      var page = req.body.page;
      var categorieSearch= req.body.topic;
      var limit = req.body.limit
      var skip = (page - 1) * limit;


      let categories = await models.Categories.find({name:categorieSearch}).skip(skip).limit(limit).sort({'createdAt':-1})
      let total = await models.Categories.find().sort({'createdAt':-1})

      res.status(200).json({
        categories: categories,
        total:total.length
      })
      return

    }catch(err){
      console.log(err)
      res.status(500).send({message: 'ocurrio un problema'})
    }
  },
  remove: async(req, res) =>{
    try{
      await models.Categories.findByIdAndDelete({_id: req.params.id})
      res.status(200).json({message: 'Categoria eliminada correctamente' })
    }catch(err){
      console.log(err)
      res.status(500).send({message: 'ocurrio un problema'})
    }
  },
}

